  // }

  render() {
    return (
      <div>
      <Navigation onRouteChange={this.onRouteChange} signed={this.state.signed}/>
      <div className="area" >
        <ul className="circles">
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                        <li></li>
                </ul>  
        </div>
        { this.state.route === 'home' ? 
            <div className="context ">
              <h1>...so I know what language you speak!</h1>
            </div> :
            (this.state.route === 'signed' ? 
            <WordForm onInputChange={this.onInputChange} onButtonClick={this.onButtonClick}/> : 
            (this.state.route === 'register' ? 
            <Register onRouteChange={this.onRouteChange}/> :
            <SignIn onRouteChange={this.onRouteChange}/>))
        }
    </div>
    );
  }

}

export default App;
